insert into books (id, title, author, genre, description) values (1, 'O Ultimo Horizonte', 'Jason Cole', 'Ficcao Cientifica', 'Em um futuro distante, a humanidade luta por sobrevivencia em uma colonia espacial prestes a colapsar. Um jovem engenheiro descobre uma ameaca alienigena desconhecida e a unica maneira de salvar a colonia.');
insert into books (id, title, author, genre, description) values (2, 'A Danca das Sombras', 'Isabella Clarke', 'Fantasia', 'Em um reino mergulhado em guerra, uma jovem com habilidades magicas precisa aprender a controlar suas forças enquanto enfrenta traicoes e criaturas da escuridao.');
insert into books (id, title, author, genre, description) values (3, 'Memorias do Amanha', 'Lucas Moreira', 'Thriller Psicologico', 'Um homem começa a ter flashes de eventos futuros e se ve envolvido em uma conspiração internacional que ameaca alterar o curso da historia.');
insert into books (id, title, author, genre, description) values (4, 'O Ultimo Poeta', 'Eduarda Almeida', 'Romance Historico', 'No periodo medieval, um poeta errante é chamado para escrever a historia de uma batalha epica, mas se ve envolvido em um romance proibido que pode mudar sua vida para sempre.');
insert into books (id, title, author, genre, description) values (5, 'Cidades Sob o Mar', 'Ricardo Santana', 'Ficcao Cientifica', 'Depois de a Terra ser devastada por cataclismos, a humanidade constroi cidades submersas. Um jovem cientista tenta descobrir o segredo por tras do misterioso desaparecimento de uma dessas cidades.');
insert into books (id, title, author, genre, description) values (6, 'As Almas Perdidas', 'Amanda Silva', 'Terror', 'Um grupo de amigos decide investigar uma casa abandonada onde, segundo lendas locais, almas de antigos moradores ainda assombram. O que eles descobrem é mais aterrador do que imaginavam.');
insert into books (id, title, author, genre, description) values (7, 'O Enigma da Esfinge', 'Fernando Ribeiro', 'Misterio', 'Um arqueologo e sua equipe desenterram um antigo templo egipcio e descobrem um enigma que remonta aos tempos dos faraos. Resolver esse misterio pode desvendar segredos do passado e mudar a historia da humanidade.');
insert into books (id, title, author, genre, description) values (8, 'As Asas do Destino', 'Maria Costa', 'Romance Fantastico', 'Em um mundo onde anjos e humanos coexistem, uma jovem com asas douradas tenta entender seu papel no destino de seu povo, enquanto se apaixona por um humano.');
insert into books (id, title, author, genre, description) values (9, 'O Legado de Orpheus', 'Hugo Pereira', 'Ficcao Historica', 'Um descendente de Orpheus, o lendario musico, descobre que sua linhagem carrega um poder ancestral. Agora, ele deve impedir que forcas sombrias usem esse poder para fins destrutivos.');
insert into books (id, title, author, genre, description) values (10, 'A Ultima Estrela', 'Clara Santos', 'Ficcao Cientifica', 'Com a destruicao iminente de seu planeta natal, um pequeno grupo de sobreviventes embarca em uma missao desesperada para encontrar um novo lar no cosmos.');
insert into books (id, title, author, genre, description) values (11, 'O Codigo da Serpente', 'Gustavo Oliveira', 'Aventura', 'Um arqueologo e uma hacker precisam unir forças para desvendar um codigo milenar escondido em artefatos antigos e impedir uma sociedade secreta de dominar o mundo.');
insert into books (id, title, author, genre, description) values (12, 'A Melodia dos Ventos', 'Lucia Martins', 'Fantasia', 'Em um mundo onde a musica tem o poder de controlar os elementos, uma jovem cantora deve aprender a controlar sua voz para evitar que forcas obscuras destruam seu reino.');
insert into books (id, title, author, genre, description) values (13, 'Sombra e Cinzas', 'Leonardo Souza', 'Thriller', 'Uma cidade devastada por incendios misteriosos guarda segredos sombrios. Um detetive, marcado por seu passado, luta para desvendar a verdade por tras dos crimes.');
insert into books (id, title, author, genre, description) values (14, 'O Guardiao das Estrelas', 'Renata Ferreira', 'Fantasia', 'Um ser imortal protege as estrelas do ceu, mas uma antiga ameaca surge para destruir todo o universo. O guardiao se ve forçado a formar uma alianca improvavel com mortais para impedir o caos.');
insert into books (id, title, author, genre, description) values (15, 'Reflexos do Passado', 'Tiago Costa', 'Suspense', 'Uma mulher começa a ter pesadelos com cenas de um crime que nunca cometeu. Ao investigar, ela descobre uma conexao com seu passado que pode ser fatal.');
insert into books (id, title, author, genre, description) values (16, 'O Despertar dos Deuses', 'Ana Lima', 'Fantasia', 'No fim dos tempos, os deuses antigos retornam a Terra para recomeçar o ciclo da humanidade. Porém, uma jovem heroina questiona se o novo reinado divino sera uma bencao ou uma maldicao.');
insert into books (id, title, author, genre, description) values (17, 'O Labirinto da Mente', 'Carlos Almeida', 'Ficcao Psicologica', 'Um psiquiatra que lida com pacientes com transtornos psicologicos começa a duvidar de sua propria sanidade quando se ve preso em um labirinto mental que nao consegue escapar.');
insert into books (id, title, author, genre, description) values (18, 'Cidade das Almas Silenciosas', 'Fernanda Martins', 'Terror', 'Uma cidade abandonada, conhecida por ser um local onde as pessoas desaparecem misteriosamente, esconde segredos que um investigador ousado vai tentar desvelar.');
insert into books (id, title, author, genre, description) values (19, 'A Heranca do Rei', 'Eduardo Pereira', 'Aventura', 'Um jovem principe, exilado desde o nascimento, descobre que é o herdeiro legitimo de um reino perdido e parte em uma jornada para recuperar seu trono.');
insert into books (id, title, author, genre, description) values (20, 'O Fim do Mundo', 'Juliana Alves', 'Distopia', 'Após um cataclismo global, um grupo de sobreviventes luta para reconstruir a sociedade. Mas uma nova ameaca surge: uma sociedade totalitaria disposta a dominar o mundo a qualquer custo.');
insert into books (id, title, author, genre, description) values (21, 'Entre Sombras e Luz', 'Marcos Oliveira', 'Romance', 'Um amor impossivel entre uma princesa e um guerreiro que pertence a uma ordem secreta de assassinos. Eles devem decidir se seu amor vale a pena enfrentar a destruicao de seus mundos.');
insert into books (id, title, author, genre, description) values (22, 'A Ultima Noite de Verao', 'Patricia Silva', 'Drama', 'Uma adolescente luta contra a perda de seu melhor amigo, refletindo sobre as escolhas da vida e o impacto das amizades na jornada do amadurecimento.');
insert into books (id, title, author, genre, description) values (23, 'O Eclipse do Destino', 'Marcelo Ferreira', 'Ficcao Cientifica', 'Quando um eclipse solar misterioso interrompe a tecnologia em todo o planeta, um cientista tenta entender os eventos que podem mudar a humanidade para sempre.');
insert into books (id, title, author, genre, description) values (24, 'O Reino do Vento', 'Simone Santos', 'Fantasia', 'Uma jovem é escolhida para governar um reino onde os ventos tem a capacidade de moldar a realidade, mas ela precisa aprender a controlar esse poder antes que ele destrua tudo o que ama.');
insert into books (id, title, author, genre, description) values (25, 'As Chaves do Inferno', 'Roberta Lima', 'Terror', 'Um antiquario descobre um conjunto de chaves antigas que abrem portas para dimensoes infernais. Agora, ele deve impedir que forcas demonicas escapem para o mundo real.');
insert into books (id, title, author, genre, description) values (26, 'A Ilha das Aguas Vivas', 'Renato Costa', 'Aventura', 'Um grupo de exploradores encontra uma ilha inexplorada onde criaturas aquaticas perigosas e um povo isolado desafiam a sobrevivencia.');
insert into books (id, title, author, genre, description) values (27, 'Vinganca de um Samurai', 'Andre Oliveira', 'Acao', 'Um samurai vive para vingar a morte de sua familia e honra, enfrentando poderosos inimigos em uma jornada solitaria de justiça.');
insert into books (id, title, author, genre, description) values (28, 'O Ultimo Suspiro', 'Lucia Pereira', 'Misterio', 'Uma detetive investiga uma serie de suicidios misteriosos, mas logo percebe que esta lidando com algo alem do que os olhos podem ver.');
insert into books (id, title, author, genre, description) values (29, 'Coraçoes de Ferro', 'Paula Santos', 'Romance Historico', 'Durante a Segunda Guerra Mundial, dois jovens apaixonam-se enquanto lidam com os horrores do conflito e as dificuldades do amor em tempos de guerra.');
insert into books (id, title, author, genre, description) values (30, 'O Guardiao dos Livros Perdidos', 'Jorge Rodrigues', 'Fantasia', 'Um jovem bibliotecario herda a responsabilidade de proteger livros magicos e proibidos. Quando um desses livros é roubado, ele precisa embarcar em uma busca para evitar que o mundo seja corrompido.');
insert into books (id, title, author, genre, description) values (31, 'A Conquista dos Deuses', 'Carlos Martins', 'Aventura', 'Um heroi lendario deve reunir um exercito de mortais e deuses para enfrentar uma ameaca cosmica que ameaca destruir o equilibrio do universo.');
insert into books (id, title, author, genre, description) values (32, 'O Julgamento de Atlan', 'Carlos Martins', 'Ficcao Cientifica', 'Em um futuro distante, uma civilizacao intergalactica é colocada à prova quando é acusada de crimes contra a propria natureza do universo.');
insert into books (id, title, author, genre, description) values (33, 'A Ultima Lamina', 'Tiago Lima', 'Acao', 'Um ex-assassino do governo é forçado a voltar à ativa para caçar uma celula terrorista com um segredo mortal.');
insert into books (id, title, author, genre, description) values (34, 'Coracao de Fogo', 'Gabriela Souza', 'Fantasia', 'Um cavaleiro com a capacidade de controlar o fogo deve liderar uma revolucao contra um rei tirano e um exercito de criaturas infernais.');
insert into books (id, title, author, genre, description) values (35, 'Lagri mas de Gelo', 'Juliana Rocha', 'Romance', 'Um romance epico entre uma princesa do norte, de um reino gelado, e um guerreiro do sul, onde as diferencas culturais e politicas ameacam sua uniao.');
insert into books (id, title, author, genre, description) values (36, 'O Codice das Sombras', 'Ricardo Almeida', 'Misterio', 'Um historiador encontra um livro misterioso que contem pistas sobre crimes antigos e conspiracoes secretas. Ele deve decifrar seu conteudo antes que os inimigos encontrem o codice.');
insert into books (id, title, author, genre, description) values (37, 'O Peso da Coragem', 'Beatriz Silva', 'Drama', 'Uma jovem enfrenta suas insegurancas e medos para seguir seus sonhos e quebrar os limites impostos pela sociedade.');
insert into books (id, title, author, genre, description) values (38, 'A Guerra dos Sonhos', 'Marcos Costa', 'Fantasia', 'No mundo dos sonhos, um exercito de criaturas esta tentando invadir a realidade. Um grupo de sonhadores especiais precisa derrotá-los antes que seja tarde demais.');
insert into books (id, title, author, genre, description) values (39, 'O Mundo Esquecido', 'Ricardo Costa', 'Aventura', 'Um arqueologo descobre uma civilizacao perdida em uma selva remota e deve decifrar seus misterios enquanto luta contra forcas que querem esconder a verdade.');
insert into books (id, title, author, genre, description) values (40, 'Ecos do Futuro', 'Renata Oliveira', 'Ficcao Cientifica', 'Um cientista desenvolve uma maquina que pode enviar mensagens ao futuro. No entanto, ele começa a receber mensagens de um futuro apocaliptico e deve agir para impedir o desastre.');